result = 0
for system in range(2, 11):
    number = 210
    nums = []
    while number > 0:
        nums.append(number % system)
        number //= system
    flag = True
    curr = nums[1] - nums[0]
    for index in range(1, len(nums)):
        if not nums[index] - nums[index-1] == curr:
            flag = False
    if flag:
        result += system
print(result)

def w(a, b):
